}

// After
void replace_memset(void *ptr, int value, size_t size) {
    uint32_t *p = (uint32_t *)ptr;
    while (size >= 4) {
        *p++ = value;
        size -= 4;
    }
    if (size > 0) {
        memset(p, value, size);
    }
}

