// Before
void min_heapify(Heap *heap, int index) {
    int left = 2 * index + 1;
    int right = 2 * index + 2;
    int smallest = index;
    if (left < heap->size && heap->data[left] < heap->data[smallest])
        smallest = left;
    if (right < heap->size && heap->data[right] < heap->data[smallest])
        smallest = right;
    if (smallest != index) {
        swap(&heap->data[index], &heap->data[smallest]);
        min_heapify(heap, smallest);
    }
}

// After
void min_heapify(Heap *heap, int index) {
    int child = 2 * index + 1;
    while (child < heap->size) {
        if (child + 1 < heap->size && heap->data[child + 1] < heap->data[child])
            child++;
        if (heap->data[child] >= heap->data[index])
            break;
        swap(&heap->data[index], &heap->data[child]);
        index = child;
        child = 2 * index + 1;
    }
}

