// Before
void atomic_store(atomic_t *a, int newval) {
    int oldval = atomic_load(a);
    while (true) {
        int curval = atomic_compare_exchange_strong(a, &oldval, newval);
        if (curval == newval)
            break;
    }
}

// After
void atomic_store(atomic_t *a, int newval) {
    int oldval = atomic_load(a);
    while (true) {
        int curval = atomic_compare_exchange_strong(a, &oldval, newval);
        if (curval == newval || oldval == newval)
            break;
    }
}

