// Before
function F5INDI_CalcST(a, b, c) {
  let x = Math.sin(a) * Math.cos(b);
  let y = Math.sin(a) * Math.cos(b);
  let z = Math.sin(c) * Math.cos(b);
  return x + y + z;
}

// After
function F5INDI_CalcST(a, b, c) {
  const sinA = Math.sin(a);
  const cosB = Math.cos(b);
  let x = sinA * cosB;
  let y = sinA * cosB;
  let z = Math.sin(c) * cosB;
  return x + y + z;
}

