// Before
void process_data(float *buf, nframes_t nframes, float *min, float *max) {
    while (nframes >= 4) {
        work = _mm_load_ps(buf);
        current_min = _mm_min_ps(current_min, work);
        current_max = _mm_max_ps(current_max, work);
        buf += 4;
        nframes -= 4;
    }
}

// After
void process_data(float *buf, nframes_t nframes, float *min, float *max) {
    while (nframes >= 16) {
        __builtin_prefetch(buf + 64, 0, 0);
        work = _mm_load_ps(buf);
        current_min = _mm_min_ps(current_min, work);
        current_max = _mm_max_ps(current_max, work);
        buf += 4;
        work = _mm_load_ps(buf);
        current_min = _mm_min_ps(current_min, work);
        current_max = _mm_max_ps(current_max, work);
        buf += 4;
        work = _mm_load_ps(buf);
        current_min = _mm_min_ps(current_min, work);
        current_max = _mm_max_ps(current_max, work);
        buf += 4;
        work = _mm_load_ps(buf);
        current_min = _mm_min_ps(current_min, work);
        current_max = _mm_max_ps(current_max, work);
        buf += 4;
        nframes -= 16;
    }
}

